package cn.poile.blog.config;import com.fasterxml.jackson.databind.ObjectMapper;import com.fasterxml.jackson.databind.SerializationFeature;import com.fasterxml.jackson.datatype.jsr310.JavaTimeModule;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;/** * LocalDate 序列化问题 * @author: yaohw * @create: 2019-10-28 23:50 **/@Configurationpublic class JsonConfig {    @Bean    public ObjectMapper serializingObjectMapper() {        ObjectMapper objectMapper = new ObjectMapper();        objectMapper.disable(SerializationFeature.WRITE_DATES_AS_TIMESTAMPS);        objectMapper.registerModule(new JavaTimeModule());        return objectMapper;    }}