mapper.select:
select outer_a.id, outer_a.original, outer_a.category_name, outer_a.category_id, outer_a.title, outer_a.summary, outer_a.content, outer_a.html_content, outer_a.cover, outer_a.status, outer_a.view_count, outer_a.comment_count, outer_a.like_count, outer_a.collect_count, outer_a.publish_time, outer_a.update_time, outer_a.reproduce, outer_a.deleted, u.id u_user_id, u.avatar, u.nickname, t.id tag_id, t.name tag_name from ( select
from article inner_a where inner_a.deleted = 0 and inner_a.id = #{id}
and inner_a.status = #{status}
) outer_a left join user u on u.id = outer_a.user_id left join article_tag a_t on a_t.article_id = outer_a.id left join tag t on t.id = a_t.tag_id

select count(1)
from article a
where a.deleted = 0

    and a.status = #{status}


    and a.title like concat('%',concat(#{title}, '%'))


    and a.category_id = #{categoryId}


    and a.publish_time >= #{start} and a.publish_time <= #{end}


    and a.id in (select a_t.article_id from article_tag a_t where a_t.tag_id = #{tagId})

select outer_a.id, outer_a.original, outer_a.user_id, outer_a.category_name, outer_a.category_id, outer_a.title, outer_a.summary, outer_a.cover, outer_a.status, outer_a.view_count, outer_a.comment_count, outer_a.like_count, outer_a.collect_count, outer_a.publish_time, outer_a.update_time, outer_a.reproduce, outer_a.deleted, u.id u_user_id, u.avatar, u.nickname, t.id tag_id, t.name tag_name from ( select
from article inner_a where inner_a.deleted = 0 and inner_a.status = 0 and inner_a.id in (select distinct inner_a_t.article_id from article_tag inner_a_t where inner_a_t.tag_id in
#{id}
) limit 0,#{limit} ) outer_a left join user u on u.id = outer_a.user_id left join article_tag a_t on a_t.article_id = outer_a.id left join tag t on t.id = a_t.tag_id
